def random_string(length: int) -> str:
    return ''.join(random.choice(ascii_letters + digits) for _ in range(length))


def get_unused_emojies(text: str, amount: int) -> list[str]:
    emojis = []
    while len(emojis) != amount:
        emoji = random.choice(EMOJIS)
        while emoji in text or emoji in emojis:
            emoji = random.choice(EMOJIS)
        emojis.append(emoji)
    return emojis


def add_decoration(tree: str, emoji: str) -> str:
    return tree.replace('{*}', f'<{emoji}>', 1)
